    bool IsSubmitting { get; set; } = false;

    async Task PlaceOrder()
    {
        IsSubmitting = true;
        try
        {

            var newOrderId = await HttpClient.PostJsonAsync<int>("orders", OrderState.Order);
            OrderState.ResetOrder();

            NavigationManager.NavigateTo($"myorders/{newOrderId}");
        }
        catch
        {
            //do nothing
        }
        finally
        {
            IsSubmitting = false;   
        }
    }

